import axios from 'axios';
import React, { useState } from 'react'
import { useHistory } from "react-router-dom";
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

import FormNav from '../Nav/FormNav';


const PetForm = (props) => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [skillOne, setSkillOne] = useState("");
    const [skillTwo, setSkillTwo] = useState("");
    const [skillThree, setSkillThree] = useState("");
    const [image, setImage] = useState();
    const [errors, setErrors] = useState({});
    const history = useHistory();


    //handler when the form is submitted
    const onSubmitHandler = event => {
        event.preventDefault();

        axios.post('http://localhost:8000/api/pets/new', {
            name,
            type,
            description,
            skillOne,
            skillTwo,
            skillThree,
            image
        })
            // Displays validiations
            .then(res => {
                console.log(res)
                if (res.data.errors) {
                    setErrors(res.data.errors)
                }
                // Clears input fields
                else {
                    setName("")
                    setType("")
                    setDescription("")
                    setSkillOne("")
                    setSkillTwo("")
                    setSkillThree("")
                    setErrors("")
                    history.push("/")
                }
            })
            .catch(err => console.log(err))
    }


    return (
        <div>
            <FormNav /><br></br><br></br><br></br>
            <h3>Know a pet needing a home?</h3>
            <form onSubmit={onSubmitHandler} className='mx-auto'>
                <Box
                    component="form"
                    sx={{
                        '& .MuiTextField-root': { m: 1, width: '25ch' }, outline: '10px',
                    }}
                    noValidate
                    autoComplete="off"
                >
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '15px' }}>
                        <TextField label="Pet Name" type="text" variant="filled" onChange={(event) => setName(event.target.value)} value={name} />
                        <p className='danger'>{errors.name ? errors.name.message : ""}</p>
                        <TextField label="Pet Type" type="text" variant="filled" onChange={(event) => setType(event.target.value)} value={type} />
                        <p className='danger'>{errors.type ? errors.type.message : ""}</p>
                    </div><br></br>

                    <TextField label="Pet Description" type="text" variant="filled" multiline rows={5} fullWidth onChange={(event) => setDescription(event.target.value)} value={description} />
                    <p className='danger'>{errors.description ? errors.description.message : ""}</p>

                    <hr />
                    <h6>Skills (Optional)</h6>
                    <p>
                        <label>Skill One: </label><br />
                        <input type="text" onChange={(event) => setSkillOne(event.target.value)} value={skillOne} />
                    </p>
                    <p>
                        <label>Skill Two: </label><br />
                        <input type="text" onChange={(event) => setSkillTwo(event.target.value)} value={skillTwo} />
                    </p>
                    <p>
                        <label>Skill Three: </label><br />
                        <input type="text" onChange={(event) => setSkillThree(event.target.value)} value={skillThree} />
                    </p>
                    <input
                        type="file"
                        name="myImage"
                        onChange={(event) => {
                            console.log(event.target.files[0]);
                            setImage(event.target.files[0]);
                        }}
                    />
                    <br></br>
                    <input type="submit" className="btn btn-success" />
                </Box>
            </form>
            <Button className="btn btn-primary" to="/">Home Page</Button>
        </div>
    )
}

export default PetForm;
