import axios from 'axios';
import React, { useState, useEffect } from 'react'
import background from "../assets/petBackground.png";
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';

import DashBoardNav from '../Nav/DashBoardNav';
import Footer from '../Footer/footer';


const PetList = (props) => {
    const [pets, setPets] = useState([]);
    const [searchInput, setSearchInput] = useState('');
    const [filteredResults, setFilteredResults] = useState([]);



    // Displays all pets
    useEffect(() => {
        axios.get('http://localhost:8000/api/pets/all')
            .then(res => {
                setPets(res.data);
            })
            .catch(err => console.error(err));
    }, []);

    const searchItems = (searchValue) => {
        setSearchInput(searchValue)
        if (searchInput !== '') {
            const filteredData = pets.filter((item) => {
                return Object.values(item).join('').toLowerCase().includes(searchInput.toLowerCase())
            })
            setFilteredResults(filteredData)
        }
        else{
            setFilteredResults(pets)
        }
    }


    return (
        <div>
            <DashBoardNav />
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                backgroundImage: `url(${background})`,
                backgroundRepeat: 'repeat',
                marginTop: '60px',
            }}>
            <Container sx={{ marginBottom:'100px' }} maxWidth="md">

                <input icon='search'
                    placeholder='Search...'
                    onChange={(e) => searchItems(e.target.value)}
                />

            <h3 style={{ marginTop: '40px', marginBottom: '20px', fontWeight: 'bold' }}>There pets are looking for a new home!</h3>
                <Grid container spacing={4}>

                    {searchInput.length > 1 ? (
                        filteredResults.map((item) => {
                            return (
                                <Card>
                                    <Card.Content>
                                        <Card.Header>{item.name}</Card.Header>
                                        <Card.Description>
                                            {item.email}
                                        </Card.Description>
                                    </Card.Content>
                                </Card>
                            )
                        })
                    ) : pets.map((pets) => (
                        <Grid item key={pets} xs={12} sm={6} md={3}>
                            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                                <CardMedia
                                    style={{height: '200px'}}
                                    component="img"
                                    image="https://source.unsplash.com/random"
                                    alt="random"
                                /><img alt='test'>{pets.image}</img>
                                <CardContent sx={{ flexGrow: 1 }}>
                                    <Typography gutterBottom variant="h5" component="h2">
                                        {pets.name}
                                    </Typography>
                                    <Typography>
                                        {pets.type}
                                    </Typography>
                                </CardContent>
                                <CardActions style={{ justifyContent: "space-between" }}>
                                    <Button color="secondary" size="small" href={`/pet/` + pets._id}>View</Button>
                                    <Button color="secondary" size="small" href={`/pet/update/` + pets._id}>Edit</Button>
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
            </div>
            <Footer />
        </div>
    );
}

export default PetList;
