import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams, Link, useHistory } from "react-router-dom";
import background from "../assets/petBackground.png";
import Button from '@mui/material/Button';
import { Paper } from '@mui/material';
import FormNav from '../Nav/FormNav';
import Footer from '../Footer/footer';

const Detail = (props) => {
    const [pets, setPets] = useState({})
    const { _id } = useParams();
    const history = useHistory();

    // Retrieves one single item by id
    useEffect(() => {
        axios.get('http://localhost:8000/api/pet/' + _id)
            .then(res => setPets(res.data))
            .catch(err => console.error(err));
    }, [_id]);

    // Deletes one pet
    const deleteProduct = (_id) => {
        axios.delete('http://localhost:8000/api/pet/delete/' + _id)
            .then(res => {
                history.push("/")
            })
            .catch(err => console.error(err));
    }


    return (
        <div>
            <FormNav />
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                backgroundImage: `url(${background})`,
                backgroundRepeat: 'repeat',
                marginTop: '60px',
            }}>

                <div>
                    <h3 style={{ marginTop: '40px', marginBottom: '20px', fontWeight: 'bold' }}>Know a pet in need of a good home?</h3>
                    <Paper
                        elevation={2}
                        style={{
                            width: '600px',
                            padding: 20,
                            marginBottom: '100px',
                            backgroundColor: "rgb(247 247 247 / 87%)"
                        }}>
                        <h1>Pet Shelter</h1>
                        <h3>Details about: {pets.name}</h3>
                        <p>Pet type: {pets.type}</p>
                        <p>Description: {pets.description}</p>
                        <p>Skills: {pets.skillOne} {pets.skillTwo} {pets.skillThree}</p>
                        <button className="btn btn-success" onClick={(e) => { deleteProduct(pets._id) }}>Adopt {pets.name}</button><br></br><br></br>
                        <Link to="/" className="btn btn-primary">Home Page</Link>
                    </Paper>
                </div>
            </div>
            <Footer />
        </div>
    )
}

export default Detail;
