import axios from 'axios';
import React, { useState, useEffect } from 'react'
import background from "../assets/petBackground.png";
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Footer from '../Footer/footer';

import { Image } from 'cloudinary-react';


const PetList = (props) => {
    const [pets, setPets] = useState([]);
    const [imageIds, setImageIds] = useState();

    const loadImages = async () => {
        try {
            const res = await fetch('http://localhost:8000/api/images');
            const data = await res.json();
            console.log(data)
            setImageIds(data);
        } catch (err) {
            console.error(err);
        }
    };



    // Displays all pets
    // useEffect(() => {
    //     // loadImages();
    //     axios.get('http://localhost:8000/api/pets/all')
    //         .then(res => {
    //             setPets(res.data);
    //         })
    //         .catch(err => console.error(err));
    // }, []);
    // useEffect(() => {
    //     loadImages();
    // }, []);

    // Filters data for search bar
    const filteredData = pets.filter((filteredPet) => {
        if (props.input === '') {
            return filteredPet;
        } else {
            return filteredPet.name.toLowerCase().includes(props.input) || filteredPet.type.toLowerCase().includes(props.input)
        }
    })


    return (
        <div>

            <div className="gallery">
                {imageIds &&
                    imageIds.map((imageId, index) => (
                        <Image
                            key={index}
                            cloudName={process.env.REACT_APP_CLOUDINARY_NAME}
                            publicId={imageId}
                            width="300"
                            crop="scale"
                        />
                    ))}
            </div>
        </div>
    );
}

export default PetList;
