import axios from 'axios';
import React, { useState } from 'react'
import { useHistory } from "react-router-dom";
import background from "../assets/petBackground.png";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import { Paper } from '@mui/material';
import FormNav from '../Nav/FormNav';
import Footer from '../Footer/footer';


const PetForm = (props) => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [skillOne, setSkillOne] = useState("");
    const [skillTwo, setSkillTwo] = useState("");
    const [skillThree, setSkillThree] = useState("");
    const [errors, setErrors] = useState({});
    const history = useHistory();

    const [fileInputState, setFileInputState] = useState('');
    const [previewSource, setPreviewSource] = useState('');
    const [selectedFile, setSelectedFile] = useState();


    //handler when the form is submitted
    const onSubmitHandler = event => {
        event.preventDefault();
        // image upload
        if (!selectedFile) return;
        const reader = new FileReader();
        reader.readAsDataURL(selectedFile);
        reader.onloadend = () => {
            console.log("Made it!!!")
            uploadImage(reader.result);
        };
        reader.onerror = () => {
            console.error('AHHHHHHHH!!');
        };

        axios.post('http://localhost:8000/api/pets/new', {
            name,
            type,
            description,
            skillOne,
            skillTwo,
            skillThree
        })
            // Displays validiations
            .then(res => {
                console.log(res)
                if (res.data.errors) {
                    setErrors(res.data.errors)
                }
                // Clears input fields
                else {
                    setName("")
                    setType("")
                    setDescription("")
                    setSkillOne("")
                    setSkillTwo("")
                    setSkillThree("")
                    setErrors("")
                    // history.push("/")
                }
            })
            .catch(err => console.log(err))
    }

    // test ======================================================
    const handleFileInputChange = (e) => {
        const file = e.target.files[0];
        previewFile(file);
        setSelectedFile(file);
        setFileInputState(e.target.value);
    };

    const previewFile = (file) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onloadend = () => {
            setPreviewSource(reader.result);
        };
    };

    // const handleSubmitFile = (e) => {
    //     e.preventDefault();
    //     if (!selectedFile) return;
    //     const reader = new FileReader();
    //     reader.readAsDataURL(selectedFile);
    //     reader.onloadend = () => {
    //         uploadImage(reader.result);
    //     };
    //     reader.onerror = () => {
    //         console.error('AHHHHHHHH!!');
    //     };
    // };

    const uploadImage = async (base64EncodedImage) => {
        console.log(base64EncodedImage)
        try {
            await fetch('/api/upload/image', {
                method: 'POST',
                body: JSON.stringify({ data: base64EncodedImage }),
                headers: { 'Content-Type': 'application/json' },
            });
            setFileInputState('');
            setPreviewSource('');
        } catch (err) {
            console.error(err);
        }
    };
    // test ======================================================



    return (
        <div>
            <FormNav />
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                backgroundImage: `url(${background})`,
                backgroundRepeat: 'repeat',
                marginTop: '60px',
            }}>

                <div>
                    <h3 style={{ marginTop: '40px', marginBottom: '20px', fontWeight: 'bold' }}>Know a pet in need of a good home?</h3>
                    <Paper
                        elevation={2}
                        style={{
                            width: '600px',
                            padding: 20,
                            marginBottom: '100px',
                            backgroundColor: "rgb(247 247 247 / 87%)"
                        }}>
                        <form onSubmit={onSubmitHandler}>
                            <div style={{
                                display: 'flex',
                                flexDirection: 'column',
                                justifyContent: 'space-between',
                                columnGap: '80px'
                            }} >
                                <h5 style={{ textAlign: 'left' }}>Pet Information</h5>
                                <TextField
                                    label="Pet Name"
                                    type="text"
                                    variant="filled"
                                    fullWidth
                                    onChange={(event) => setName(event.target.value)} value={name} />
                                <p className='danger'>{errors.name ? errors.name.message : ""}</p>
                                <TextField
                                    label="Pet Type"
                                    type="text" variant="filled"
                                    fullWidth
                                    onChange={(event) => setType(event.target.value)} value={type} />
                                <p className='danger'>{errors.type ? errors.type.message : ""}</p>
                                <TextField
                                    label="Pet Description"
                                    type="text"
                                    variant="filled"
                                    multiline
                                    rows={4}
                                    fullWidth
                                    onChange={(event) => setDescription(event.target.value)} value={description} />
                                <p className='danger'>{errors.description ? errors.description.message : ""}</p>
                                <hr />
                                <h5 style={{ textAlign: 'left' }}>Pet Skills <span style={{ color: 'grey' }} >(Optional)</span></h5>
                                <TextField
                                    label="Skill One"
                                    type="text"
                                    variant="filled"
                                    fullWidth
                                    onChange={(event) => setSkillOne(event.target.value)} value={skillOne} />
                                <p></p>
                                <TextField
                                    label="Skill Two"
                                    type="text"
                                    variant="filled"
                                    fullWidth
                                    onChange={(event) => setSkillTwo(event.target.value)} value={skillTwo} />
                                <p></p>
                                <TextField
                                    label="Skill Three"
                                    type="text"
                                    variant="filled"
                                    fullWidth
                                    onChange={(event) => setSkillThree(event.target.value)} value={skillThree} />
                                <p></p>

                                <input
                                    id="fileInput"
                                    type="file"
                                    name="image"
                                    onChange={handleFileInputChange}
                                    value={fileInputState}
                                    className="form-input"
                                />

                                <br></br>
                                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                                    <Button
                                        href="/"
                                        variant="text"
                                        style={{ color: 'grey' }}>Cancel
                                    </Button>
                                    <Button
                                        type="submit"
                                        variant="contained"
                                        style={{ backgroundColor: 'rgb(248, 181, 161)', width: '140px' }}>Submit
                                    </Button>
                                </div>
                            </div>
                            {previewSource && (
                                <img
                                    src={previewSource}
                                    alt="chosen"
                                    style={{ height: '300px' }}
                                />
                            )}
                        </form>
                    </Paper>
                </div>
            </div>
            <Footer />
        </div>
    )
}

export default PetForm;
