import axios from 'axios';
// import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react'

import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';

import MainNav from '../Nav/MainNav';


const PetList = (props) => {
    const [pets, setPets] = useState([]);


    // Displays all pets
    useEffect(() => {
        axios.get('http://localhost:8000/api/pets/all')
            .then(res => {
                setPets(res.data);
            })
            .catch(err => console.error(err));
    }, []);


    return (
        <div>
            <MainNav />
            <Container sx={{ py: 8 }} maxWidth="md">
            <Typography variant="h6" sx={{ margin: 2}}>
                These pets are available for adoption
            </Typography>
                <Grid container spacing={4}>
                    {pets.map((pets) => (
                        <Grid item key={pets} xs={12} sm={6} md={3}>
                            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                                <CardMedia
                                    // style={{height: '100%'}}
                                    component="img"
                                    image="https://source.unsplash.com/random"
                                    alt="random"
                                />
                                <CardContent sx={{ flexGrow: 1 }}>
                                    <Typography gutterBottom variant="h5" component="h2">
                                        {pets.name}
                                    </Typography>
                                    <Typography>
                                        {pets.type}
                                    </Typography>
                                </CardContent>
                                <CardActions style={{ justifyContent: "space-between" }}>
                                    <Button color="secondary" size="small" href={`/pet/` + pets._id}>View</Button>
                                    <Button color="secondary" size="small" href={`/pet/update/` + pets._id}>Edit</Button>
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
        </div>
    );
}

export default PetList;
