import axios from 'axios';
import React, { useState } from 'react'
import { useHistory } from "react-router-dom";
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Paper } from '@mui/material';

import FormNav from '../Nav/FormNav';


const PetForm = (props) => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [skillOne, setSkillOne] = useState("");
    const [skillTwo, setSkillTwo] = useState("");
    const [skillThree, setSkillThree] = useState("");
    const [image, setImage] = useState();
    const [errors, setErrors] = useState({});
    const history = useHistory();


    //handler when the form is submitted
    const onSubmitHandler = event => {
        event.preventDefault();

        axios.post('http://localhost:8000/api/pets/new', {
            name,
            type,
            description,
            skillOne,
            skillTwo,
            skillThree,
            image
        })
            // Displays validiations
            .then(res => {
                console.log(res)
                if (res.data.errors) {
                    setErrors(res.data.errors)
                }
                // Clears input fields
                else {
                    setName("")
                    setType("")
                    setDescription("")
                    setSkillOne("")
                    setSkillTwo("")
                    setSkillThree("")
                    setErrors("")
                    history.push("/")
                }
            })
            .catch(err => console.log(err))
    }


    return (
        <div>
            <FormNav /><br></br><br></br><br></br>
            <h3>Know a pet needing a home?</h3>
            <div style={{ display: 'flex', justifyContent: 'center' }}>
                <Box
                    component="span"
                    sx={{
                        width: '600px',
                        border: '2px black'
                    }}
                >
                    <Paper elevation={2} style={{ padding: 20, backgroundColor: "rgb(247 247 247 / 87%)" }}>
                        <form onSubmit={onSubmitHandler} className='mx-auto'>                           
                            <h6 style={{ textAlign:'left'}}>Pet Information</h6>
                            <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
                                <TextField label="Pet Name" type="text" variant="filled" fullWidth onChange={(event) => setName(event.target.value)} value={name} />
                                <p className='danger'>{errors.name ? errors.name.message : ""}</p>
                                <TextField label="Pet Type" type="text" variant="filled" fullWidth onChange={(event) => setType(event.target.value)} value={type} />
                                <p className='danger'>{errors.type ? errors.type.message : ""}</p>
                            </div><br></br>
                            <TextField label="Pet Description" type="text" variant="filled" multiline rows={4} fullWidth onChange={(event) => setDescription(event.target.value)} value={description} />
                            <p className='danger'>{errors.description ? errors.description.message : ""}</p>
                            <hr />
                            <h6 style={{ textAlign:'left'}}>Pet Skills (Optional)</h6>
                            <TextField label="Skill One" type="text" variant="filled" fullWidth onChange={(event) => setSkillOne(event.target.value)} value={skillOne} /><br></br><br></br>
                            <TextField label="Skill Two" type="text" variant="filled" fullWidth onChange={(event) => setSkillTwo(event.target.value)} value={skillTwo} /><br></br><br></br>
                            <TextField label="Skill Three" type="text" variant="filled" fullWidth onChange={(event) => setSkillThree(event.target.value)} value={skillThree} /><br></br><br></br>
                            <input
                                type="file"
                                name="myImage"
                                onChange={(event) => {
                                    console.log(event.target.files[0]);
                                    setImage(event.target.files[0]);
                                }}
                            /><br></br><br></br>
                            <Button type="submit" className="btn btn-success" >Submit</Button>
                        </form>
                    </Paper>
                </Box>
            </div>
        </div>
    )
}

export default PetForm;
